// }
//   </div> 
        <ContentList>
     {/* {  data && data.length>0 && data.map((item)=><h3>{item.name}</h3>)} */}
             <div className="boxWrap">

               { data.map((item)=> <div className="img"><img src="{item.image}"/></div>)}
                {  data.map((item)=><div className="username">{item.name}</div>)}
            </div>
             <div className="desc">this is description</div> 
        </ContentList>
    )
}

export default Content;
